import React, { Component } from 'react'

export default class StateInClass extends Component {
    constructor(props){
        super(props);
        this.state = {
            name: props.name,
            counter: props.counter, 
        };
    }
    render() {
        return <div><p><b>Name : </b>{this.state.name}</p> <p><b></b>Counter = {this.state.counter} </p></div>;
        
    }
}
